import React from 'react';
import styled from 'styled-components';

const DivCard = styled.div`
background-color: #dedbdbc7;
display: flex;
flex-direction: column;
border-radius: 10px;
overflow: hidden;
&:hover{
   box-shadow: 4px 4px 15px #000000a3;
   background-color: rgba(179, 12, 12, 0.862);
   cursor: pointer;
}
`;

const SizeH = styled.h2`
font-size: 2vw;
`;

const Imagen = styled.img`
object-fit: contain;
width: 100%;
`;

const Button = styled.button`
position: absolute;
border: none;
border-radius: 10px 0 0 0;
&:hover{
   background-color: #cfcece;
   cursor: pointer;
}
`;

export default function Card(props) {
   return (
      <DivCard key={props.id}>
         <Imagen src={props.image} alt='imagen' />
         <Button onClick={props.onClose}>X</Button>
         <SizeH>{props.name}</SizeH>
         {/* <SizeH>{props.status}</SizeH> */}
         {/* <SizeH>{props.species}</SizeH> */}
         <SizeH>{props.gender}</SizeH>
         {/* <SizeH>{props.origin.name}</SizeH> */}
      </DivCard>
   );
}
