import React from 'react';
import styled from 'styled-components';
import {Link} from 'react-router-dom'

const DivCard = styled.div`
max-width: 315px;
background-color: #ffffff;
display: flex;
flex-direction: column;
border-radius: 10px;
overflow: hidden;
box-shadow: 5px 5px 15px #00000019;
&:hover{
   box-shadow: 5px 5px 15px #0000006c;
   background-color: rgba(179, 12, 12, 0.862);
   cursor: pointer;
}
`;

const SizeH3 = styled.h2`
font-size: 70%;
text-decoration: none;
color: white;
`;

const Imagen = styled.img`
object-fit: contain;
width: 100%;
`;

const Button = styled.button`
position: absolute;
border: none;
border-radius: 10px 0 0 0;
&:hover{
   background-color: rgba(179, 12, 12, 0.862);
   cursor: pointer;
}
`;

export default function Card({character, onClose}) {
   const { id, name, gender, image } = character;
   return (
      <DivCard key={id}>
         <Imagen src={image} alt='imagen' />
         <Button onClick={() => onClose(id)}>X</Button>
         <Link to={`/Detail/${id}`}>
            <SizeH3>{name}</SizeH3>
         </Link>
         {/* <SizeH>{status}</SizeH> */}
         {/* <SizeH>{species}</SizeH> */}
         <SizeH3>{gender}</SizeH3>
         {/* <SizeH>{origin.name}</SizeH> */}
      </DivCard>
   );
}
