export default function Validate(userData, setErrors, errors, property) {
    console.log(property)
    console.log(errors);
    console.log(userData[property]);
    if(!userData[property]) {
        setErrors({...errors, [property]: `${property} sin completar`});
    }

    else {
        if (property === 'username') {
            if (userData.username.length >= 35) setErrors({...errors, [property]: 'username demasiado largo', boolean: false, length: true});
            if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(userData.username)) {
                setErrors({...errors, [property]: '', boolean: true, length: false});
            }
            else setErrors({...errors, [property]: 'username inválido', boolean: false, length: false});
        }
    }
}
