const initialState = {
    myFavorites: [],
    allCharacters: []
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case "ADD_FAV":
            return {
                ...state, allCharacters: [...state.allCharacters, action.payload],
                ...state, myFavorites: [...state.myFavorites, action.payload]
            };

        case "REMOVE_FAV":
            return {...state, myFavorites: state.myFavorites.filter((char) => char.id !== Number(action.payload))}

        case 'FILTER':
            return {...state, myFavorites: state.allCharacters.filter((char) => char.gender === action.payload)}

        case "ORDER":
            const ordered = state.allCharacters.sort((a, b) => {return a.id - b.id});
            console.log(state)
            if(action.payload === 'B') ordered.reverse();
            return {...state, myFavorites: ordered};

        default:
            return {...state};
    }
};

export default rootReducer;
