const initialState = {
    myFavorites: [],
    allCharacters: []
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case "ADD_FAV":
            return {
                ...state, allCharacters: [...state.allCharacters, action.payload],
                ...state, myFavorites: [...state.myFavorites, action.payload]
            };

        case "REMOVE_FAV":
            return {...state, myFavorites: state.myFavorites.filter((char) => char.id !== Number(action.payload))}

        case 'FILTER':
            return {...state, myFavorites: state.allCharacters.filter((char) => char.gender !== action.payload)}

        case "ORDER":
            const orden = [];
            const getId = state.allCharacters.map(char => char.id);
            getId.sort(function(a, b){return a - b});
            for (let i = 0; i < getId.length; i++) {
                orden.push((state.allCharacters.filter((char) => char.id === getId[i])))
            }
            if(action.payload === 'B') {orden.reverse()}
            return {...state, myFavorites: orden}

        default:
            return {...state};
    }
};

export default rootReducer;
